package b.flow.control.ifDemo;

public class Demo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int r = (int)(Math.random()*11);
		System.out.println(r);
		//if construct:
		
		if(r>5) {
		System.out.println("Big");		
		}else {
		System.out.println("Small");
		}

	}

}
